public void PageFooter_Format()
{
	if (_sPartnerOrderNumber != null && _sPartnerOrderNumber.Length > 0 && _sPartnerOrderNumber.Contains("/"))
		((TextBox)rpt.Sections["PageFooter"].Controls["txtPageFooterFactureNumber"]).Text = _sPartnerOrderNumber.Substring(_sPartnerOrderNumber.IndexOf("/") + 1);
	else
		((TextBox)rpt.Sections["PageFooter"].Controls["txtPageFooterFactureNumber"]).Text = "";
}
